// Extract coordinates for search location
const searchData = $('Setup Parameters').first().json;
const kg = $input.first().json.knowledge_graph || {};
const answer = $input.first().json.answer_box || {};

// Try to get coordinates from SerpAPI response
let lat, lng;

if (kg.coordinates) {
  lat = kg.coordinates.latitude;
  lng = kg.coordinates.longitude;
} else if (answer.answer && answer.answer.includes('¬∞')) {
  const match = answer.answer.match(/(-?\d+\.\d+)¬∞?\s*([NS]),?\s*(-?\d+\.\d+)¬∞?\s*([EW])/);
  if (match) {
    lat = parseFloat(match[1]);
    lng = parseFloat(match[3]);
    if (match[2] === 'S') lat = -lat;
    if (match[4] === 'W') lng = -lng;
  }
}

// Comprehensive fallback coordinates for major cities
if (!lat || !lng) {
  const fallbacks = {
    'Casablanca, Morocco': [33.5731, -7.5898],
    'Rabat, Morocco': [34.0209, -6.8416],
    'Meknes, Morocco': [33.8675, -5.5382],
    'Fez, Morocco': [34.0181, -5.0078],
    'Marrakech, Morocco': [31.6295, -7.9811],
    'Paris, France': [48.8566, 2.3522],
    'Lyon, France': [45.7640, 4.8357],
    'Marseille, France': [43.2965, 5.3698],
    'Nice, France': [43.7102, 7.2620],
    'Madrid, Spain': [40.4168, -3.7038],
    'Barcelona, Spain': [41.3851, 2.1734],
    'Valencia, Spain': [39.4699, -0.3763],
    'Seville, Spain': [37.3891, -5.9845],
    'New York, United States': [40.7128, -74.0060],
    'Los Angeles, United States': [34.0522, -118.2437],
    'Chicago, United States': [41.8781, -87.6298],
    'Miami, United States': [25.7617, -80.1918],
    'London, United Kingdom': [51.5074, -0.1278],
    'Manchester, United Kingdom': [53.4808, -2.2426],
    'Birmingham, United Kingdom': [52.4862, -1.8904],
    'Liverpool, United Kingdom': [53.4084, -2.9916],
    'Berlin, Germany': [52.5200, 13.4050],
    'Munich, Germany': [48.1351, 11.5820],
    'Hamburg, Germany': [53.5511, 9.9937],
    'Frankfurt, Germany': [50.1109, 8.6821],
    'Rome, Italy': [41.9028, 12.4964],
    'Milan, Italy': [45.4642, 9.1900],
    'Naples, Italy': [40.8518, 14.2681],
    'Florence, Italy': [43.7696, 11.2558],
    'Toronto, Canada': [43.6532, -79.3832],
    'Vancouver, Canada': [49.2827, -123.1207],
    'Montreal, Canada': [45.5017, -73.5673],
    'Calgary, Canada': [51.0447, -114.0719],
    'Dubai, UAE': [25.2048, 55.2708],
    'Abu Dhabi, UAE': [24.4539, 54.3773],
    'Sydney, Australia': [-33.8688, 151.2093],
    'Melbourne, Australia': [-37.8136, 144.9631]
  };

  const coords = fallbacks[searchData.search_location] || [40.7128, -74.0060];
  lat = coords[0];
  lng = coords[1];
}

return {
  ...searchData,
  coordinates_string: `@${lat},${lng},13z`
};

// This code processes both initial and paginated results.

// Get static parameters
const searchDataFromSetup = $('Setup Parameters').first().json;
const maxDesired = searchDataFromSetup.max_leads;
const query = searchDataFromSetup.query;
const search_location = searchDataFromSetup.search_location;
const coordinates_string = $('Extract Coordinates').first().json.coordinates_string;

// Get API results from the node's direct input
const mapResults = $input.first().json;
const results = mapResults.local_results || [];

// Get previously collected leads.
// The data comes from the node before the HTTP Request that might have lost it.
// If that node hasn't run yet, it means this is the first execution, so we have no existing leads.
let existingLeads = [];
try {
  existingLeads = $('Continue or Finalize?').first().json.all_leads || [];
} catch (e) {
  // 'Continue or Finalize?' has not run yet. This is expected on the first pass.
  existingLeads = [];
}

console.log(`=== PROCESSING RESULTS ===`);
console.log(`Found ${results.length} new results`);
console.log(`Existing leads: ${existingLeads.length}`);
console.log(`Target: ${maxDesired}`);

// Process each business with complete data extraction
const newLeads = results.map((business, index) => {
  const businessName = business.title || 'Unknown';
  const website = business.website || '';
  const phone = business.phone ? "'" + business.phone.replace(/[^\d\+\-\(\)\s]/g, '') : '';
  const rating = business.rating || 0;
  const reviews = business.reviews || 0;

  // Generate email from website domain
  let email = '';
  if (website) {
    try {
      const domain = website.replace(/^https?:\/\//, '').replace(/\/.*$/, '').replace(/^www\./, '');
      email = `info@${domain}`;
    } catch (e) {
      email = '';
    }
  }

  // Extract social media links from business data
  const links = business.links || {};
  let facebook = '', instagram = '', linkedin = '', twitter = '';

  Object.values(links).forEach(link => {
    const url = typeof link === 'string' ? link : (link.link || '');
    if (url.includes('facebook.com')) facebook = url;
    if (url.includes('instagram.com')) instagram = url;
    if (url.includes('linkedin.com')) linkedin = url;
    if (url.includes('twitter.com') || url.includes('x.com')) twitter = url;
  });

  // Generate probable social media if not found but we have website
  if (website && businessName && !facebook) {
    const cleanName = businessName.toLowerCase().replace(/[^a-z0-9]/g, '').substring(0, 15);
    if (cleanName.length > 3) {
      facebook = `https://facebook.com/${cleanName}`;
      instagram = `https://instagram.com/${cleanName}`;
    }
  }

  // Enhanced lead scoring algorithm (0-100 points)
  let leadScore = 15; // Base score
  if (website) {
    leadScore += 35;
    if (website.includes('https://')) leadScore += 5;
  }
  if (phone && phone !== "'") leadScore += 20;
  if (reviews > 0) {
    leadScore += 10;
    if (rating >= 4.5) leadScore += 15;
    else if (rating >= 4.0) leadScore += 10;
    else if (rating >= 3.5) leadScore += 5;
  }
  if (business.hours) leadScore += 5;
  if (business.address && business.address.length > 10) leadScore += 5;
  if (reviews >= 100) leadScore += 5;
  else if (reviews >= 50) leadScore += 3;

  leadScore = Math.min(leadScore, 100);

  // Quality tier based on lead score
  let qualityTier = 'Poor';
  if (leadScore >= 85) qualityTier = 'Excellent';
  else if (leadScore >= 70) qualityTier = 'Good';
  else if (leadScore >= 55) qualityTier = 'Average';

  return {
    business_name: businessName,
    phone: phone,
    website: website,
    email: email,
    address: business.address || '',
    rating: rating,
    reviews: reviews,
    hours: business.hours || '',
    google_maps_link: business.place_id ? 
      `https://www.google.com/maps/place/?q=place_id:${business.place_id}` :
      `https://www.google.com/maps/search/${encodeURIComponent(businessName)}`,
    lead_score: leadScore,
    quality_tier: qualityTier,
    facebook: facebook,
    instagram: instagram,
    linkedin: linkedin,
    twitter: twitter,
    category: business.type || query,
    search_query: query,
    search_location: search_location,
    place_id: business.place_id || '',
    position: existingLeads.length + index + 1
  };
});

// Remove duplicates and combine
const allLeadsMap = new Map();
existingLeads.forEach(lead => {
  const key = `${lead.business_name}|${lead.address}`.toLowerCase();
  allLeadsMap.set(key, lead);
});
newLeads.forEach(lead => {
  const key = `${lead.business_name}|${lead.address}`.toLowerCase();
  if (!allLeadsMap.has(key)) {
    allLeadsMap.set(key, lead);
  }
});
const allLeads = Array.from(allLeadsMap.values());
const totalCollected = allLeads.length;

// Check SerpAPI pagination for next page
const paginationInfo = mapResults.serpapi_pagination || {};
const nextPageUrl = paginationInfo.next || '';
const hasMorePages = !!nextPageUrl;

console.log(`Total collected: ${totalCollected}/${maxDesired}`);
console.log(`Has more pages: ${hasMorePages}`);

// Decision logic for pagination
const needMoreLeads = totalCollected < maxDesired;
const shouldContinue = needMoreLeads && hasMorePages && results.length > 0;

console.log(`Should continue: ${shouldContinue}`);

if (shouldContinue) {
  // Return single object for pagination continuation
  return {
    // Pass along all necessary data for the loop
    query: query,
    city: searchDataFromSetup.city,
    country: searchDataFromSetup.country,
    max_leads: maxDesired,
    search_location: search_location,
    all_leads: allLeads, // <-- This is the crucial state being passed
    next_page_url: nextPageUrl,
    continue_search: true,
    coordinates_string: coordinates_string,
    progress: `Collected ${totalCollected}/${maxDesired} leads`
  };
} else {
  // Search complete - return final results as array
  const finalLeads = allLeads
    .sort((a, b) => b.lead_score - a.lead_score)
    .slice(0, maxDesired);

  console.log(`=== SEARCH COMPLETE ===`);
  console.log(`Returning ${finalLeads.length} leads`);

  return finalLeads;
}
